import { GoogleGenAI } from '@google/genai';
import React, { useEffect, useMemo, useRef, useState } from 'react';

// ÁßªÈô§‰∫Ü TypeScript ÁöÑÂûãÂà•ÂÆöÁæ©
// export type Part = { text: string };
// export type ChatMsg = { role: 'user' | 'model'; parts: Part[] };

export default function AItest() {
  const [model, setModel] = useState('gemini-2.5-flash');
  const [apiKey, setApiKey] = useState('');
  // ÁßªÈô§‰∫Ü useState ÁöÑÂûãÂà•Ë®ªÈáã <ChatMsg[]>
  const [history, setHistory] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  // ÁßªÈô§‰∫Ü useState ÁöÑÂûãÂà•Ë®ªÈáã <'chat' | 'quiz'>
  const [mode, setMode] = useState('chat');
  const [error, setError] = useState('');
  // ÁßªÈô§‰∫Ü useRef ÁöÑÂûãÂà•Ë®ªÈáã <HTMLDivElement | null>
  const listRef = useRef(null);

  // È†êË®≠Ê≠°ËøéË®äÊÅØ
  useEffect(() => {
    setHistory([
      {
        role: 'model',
        parts: [
          {
            text: 'üçø Ê≠°ËøéÂÖâËá®„ÄåÈõªÂΩ±ÈùàÈ≠ÇÊ∏¨È©óÂÆ§„ÄçÔºÅÂëäË®¥Êàë‰Ω†ÊÉ≥Áúã‰ªÄÈ∫ºÊ®£ÁöÑÈõªÂΩ±ÔºåÊàñÈñãÂßãÊ∏¨È©óËÆìÊàëÂπ´‰Ω†ÊâæÂà∞ÂëΩ‰∏≠Ë®ªÂÆöÁöÑÁâáÂñÆ üé•',
          },
        ],
      },
    ]);
  }, []);

  // Ëá™ÂãïÊªæÂãïÂà∞Â∫ï
  useEffect(() => {
    const el = listRef.current;
    if (el) el.scrollTop = el.scrollHeight;
  }, [history, loading]);

  const ai = useMemo(() => {
    try {
      // ÁßªÈô§‰∫Ü new GoogleGenAI ÁöÑÂûãÂà•Ë®ªÈáã { apiKey }
      return apiKey ? new GoogleGenAI({ apiKey }) : null;
    } catch {
      return null;
    }
  }, [apiKey]);

  // ÁßªÈô§‰∫ÜÂèÉÊï∏ message ÁöÑÂûãÂà•Ë®ªÈáã string
  async function sendMessage(message) {
    const content = (message ?? input).trim();
    if (!content || loading) return;
    if (!ai) {
      setError('AIzaSyBR8DoAiKh8DduFVXvJONHUsnxwv1GfAFg');
      return;
    }

    setError('');
    setLoading(true);
    // ÁßªÈô§‰∫Ü newHistory ÁöÑÂûãÂà•Ë®ªÈáã
    const newHistory = [...history, { role: 'user', parts: [{ text: content }] }];
    setHistory(newHistory);
    setInput('');

    try {
      // ÁßªÈô§‰∫Ü err ÁöÑÂûãÂà•Ë®ªÈáã : any
      const resp = await ai.models.generateContent({
        model,
        contents: [
          {
            role: 'user',
            parts: [
              {
                text:
                  mode === 'quiz'
                    ? '‰Ω†ÊòØ‰∏Ä‰ΩçÈõªÂΩ±ÈùàÈ≠ÇÂ∞éÂ∏´ÔºåÈÄèÈÅé‰∏ÄÊ≠•Ê≠•ÊèêÂïè‰ΩøÁî®ËÄÖÁöÑÊÉÖÁ∑í„ÄÅÁØÄÂ•è„ÄÅÈ¢®Ê†ºÂÅèÂ•ΩÔºåÊúÄÂæåÊé®Ëñ¶3ÈÉ®ÊúÄÈÅ©ÂêàÁöÑÈõªÂΩ±„ÄÇ'
                    : '‰Ω†ÊòØ‰∏Ä‰ΩçÈõªÂΩ±ËàáÂΩ±ÈõÜÊé®Ëñ¶Â∞àÂÆ∂ÔºåÊ†πÊìö‰ΩøÁî®ËÄÖÁöÑÊèèËø∞Êé®Ëñ¶ÂêàÈÅ©ÁöÑ‰ΩúÂìÅ„ÄÇ',
              },
            ],
          },
          ...newHistory,
        ],
      });

      const reply = resp.text || '[Ê≤íÊúâÂõûË¶ÜÂÖßÂÆπ]';
      setHistory((h) => [...h, { role: 'model', parts: [{ text: reply }] }]);
    } catch (err) {
      setError(err?.message || String(err));
    } finally {
      setLoading(false);
    }
  }

  // ÁßªÈô§‰∫ÜÂèÉÊï∏ text ÁöÑÂûãÂà•Ë®ªÈáã string
  function renderMarkdownLike(text) {
    return text.split(/\n/).map((line, i) => (
      <div key={i} style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
        {line}
      </div>
    ));
  }

  // Â∫ïÈÉ®Âø´ÈÄüÈ°åÁõÆÈÅ∏È†Ö
  // ÁßªÈô§‰∫Ü quickPrompts ÁöÑÂûãÂà•Ë®ªÈáã
  const quickPrompts = [
    'Âπ´ÊàëÊé®Ëñ¶ÂπæÈÉ®ÊúÄËøëÂ•ΩÁúãÁöÑ Netflix ÂΩ±ÈõÜ',
    'ÊàëÊÉ≥ÁúãÊ∫´È¶®ÂèàÁôÇÁôíÁöÑÈõªÂΩ±ÔºåÊúâ‰ªÄÈ∫ºÊé®Ëñ¶Ôºü',
    'Âπ´ÊàëÊâæ‰∏Ä‰∫õÂÉè„ÄäÊ¨äÂäõÈÅäÊà≤„ÄãÁöÑÊá∏ÁñëÁâá',
    'Êé®Ëñ¶ÂπæÈÉ®Á∂ìÂÖ∏ÁöÑÊÑõÊÉÖÈõªÂΩ±',
    'ÊúâÊ≤íÊúâ 2024 Âπ¥Êñ∞‰∏äÊò†ÁöÑÁßëÂπªÁâáÔºü',
  ];

  return (
    <div style={styles.wrap}>
      <div style={styles.card}>
        {/* Ê®ôÈ°å + Ê®°ÂºèÂàáÊèõ */}
        <div style={styles.header}>
          <h2>üé¨ ÈõªÂΩ±ÈùàÈ≠ÇÊ∏¨È©óÂÆ§</h2>
          <button
            onClick={async () => {
              const next = mode === 'quiz' ? 'chat' : 'quiz';
              setMode(next);
              if (next === 'quiz') {
                await sendMessage(
